import React, { Component } from "react";
import Button from "./Button";
import { requestAllApis } from "./RequestAPi";

export default class Posts extends Component {
  state = {
    posts: [],
    allPosts:[],
    page: 0,
    postsPerPage: 2,
  };

  componentDidMount() {
    this.requestApi();
    console.log('carregou')
  }


  requestApi = async () => {
    const  { page, postsPerPage } = this.state;
    const postsAndPhotos = await requestAllApis();

    this.setState({
      // definindo qtos posts serão exibidos por vez
      posts: postsAndPhotos.slice(page, postsPerPage),
      allPosts: postsAndPhotos });
  };

  loadMorePosts = () => {
    const { allPosts, page, postsPerPage, posts } = this.state
    const nextPage = page + postsPerPage;
    const nextPosts = allPosts.slice(nextPage, nextPage + postsPerPage);
    posts.push(...nextPosts);
    this.setState({ posts, page: nextPage })
  };

  render() {
    const { posts } = this.state;
    return (
      <section className="container">
        <div className="posts">
          {posts.map((post) => (
            <div key={post.id} className="post">
                <img src={post.cover} alt={post.title} />
              <div className="post-content">
                <h1>{post.title}</h1>
                <p>{post.body}</p>
              </div>
            </div>
          ))}
        </div>
        <Button loadMorePost={ this.loadMorePosts } 
        name="Laura"/>
      </section>
    );
  }
}
